import { ApolloError } from 'apollo-boost';
import React, { ReactNode } from 'react';
import { NavLink } from 'react-router-dom';
import { CircularProgress } from '@material-ui/core';

type RecipesByIngredientProps = {
    loading: boolean,
    data: any,
    error: ApolloError | undefined,
    called: boolean
}


function renderIngredeints(title: string, ingredientList: [{original: string}]): ReactNode {
    return ingredientList.length > 0 && (
        <article className="media">
        <strong>{title}</strong>
        <ul>{ingredientList.map(({original}, i) => <li key={i}><i>{original}</i></li>)}</ul>
        </article>
    )
}

export default function RecipesByIngredient({ called, loading, data, error }: RecipesByIngredientProps) {
    return (
        <div className="container">
        { called && loading && <div className="is-centered"><CircularProgress /></div> }
        {   
            data && data.recipesByIngredients.length > 0 && !loading && !error &&
            <div className="container" >{data.recipesByIngredients.map(({ id, title, missedIngredients, usedIngredients }: any, i: number) => {
                return (
                    <div key={i} className="box">
                        <div className="box">
                            <strong className="title">{title}</strong>
                            {renderIngredeints('Used Ingredients', usedIngredients)}
                            {renderIngredeints('Missed Ingredients', missedIngredients)}
                        </div>
                        <div><NavLink to={`/recipe/${id}`}>View Recipe</NavLink></div>
                    </div>
                )
            })}</div>
        }
        </div>
    );
}
