import React from 'react';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import { AuthType } from '../navbar.model';
import { useMutation } from '@apollo/react-hooks';
import { SIGNUP, LOGIN } from './signup_query';
import { formatError } from '../../util/helpers/formatError';
import Dialog from '@material-ui/core/Dialog';

type AuthProps = {
    loadUser: () => void,
    authType: AuthType,
}

function onError() {}

function req(type: AuthType) {
    const reqMap = new Map<AuthType, () => void>([
        [AuthType.LOGIN, () => {}],
        [AuthType.SIGNUP, () => {}],
    ])
}

function SignUp({ loadUser, authType }: AuthProps) {
    const [open, setOpen] = React.useState(false);
    const options = {
        onCompleted: () => {
            loadUser()
            setOpen(false)
        },
        onError
    }
    const [ signup, { error: regError } ] = useMutation(SIGNUP, options);
    const [ login, { error: logError } ] = useMutation(LOGIN, options);
    const call =  async (type: AuthType, config: { variables: any }) => {
        switch (type) {
            case AuthType.LOGIN:
                return await login(config);
            case AuthType.SIGNUP:
                return await signup(config);
        }
    } 

    const error = regError ?? logError;

    return (
        <>
            <button className="button">{authType === AuthType.SIGNUP ? 'Sign Up' : 'Log In' }</button>
            <Dialog open={open}>
                <Formik
                    initialValues={{ name: '', password: ''}}
                    onSubmit={async (values, { setSubmitting }) => {
                        await call(authType, {variables: values});
                        setSubmitting(false);
                    }}
                    validate={(values) => {
                        const required = ['name', 'password'];
                        const errors = Object.entries(values)
                            .filter(([key, val]) => required.some(r => r === key && val === ''))
                            .map(([key]) => ([key, 'This field is required']))
                        return Object.fromEntries(errors);
                    }}
                >
                    {({isSubmitting}) => (
                        <Form>
                            <Field name="name" type="text"></Field>
                            <ErrorMessage name="name" component="div"></ErrorMessage>
                            <Field name="password" type="password"></Field>
                            <ErrorMessage name="password" component="div"></ErrorMessage>
                            <button type="submit" disabled={isSubmitting}>
                                Sign Up
                            </button>
                            {error && formatError(error)}
                        </Form>
                    )}
                </Formik>
            </Dialog>
        </>
    );
}

export default SignUp;
