import React from 'react';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import { useMutation } from '@apollo/react-hooks';
import { LOGIN, USER_FRAGMENT } from './login_query';
import { ApolloError } from 'apollo-boost';

type LoginProps = {
    loadUser: () => void,
}

function formatError(error: ApolloError): string {
    const splitMessage = error?.message.split(':');
    const message =  splitMessage.length > 1 ? splitMessage[1] : splitMessage[0];
    return message.trim();
}

function Login({loadUser}: LoginProps) {
    const [ login, { error } ] = useMutation(LOGIN, {
        onError: () => {},
        onCompleted: (data) => {
            console.log(data);
            loadUser();
        }
    });

    return (
        <Formik
            initialValues={{name: '', password: ''}}
            onSubmit={(values) => login({variables: values})}
        >
            <Form>
                <Field name="name"></Field>
                <ErrorMessage name="name"></ErrorMessage>
                <Field type="password" name="password"></Field>
                <ErrorMessage name="password"></ErrorMessage>
                <button>Login</button>
                {error && <span>{formatError(error)}</span>}
            </Form>
        </Formik>
    )
}

export default Login;
