import React from 'react';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import { useMutation } from '@apollo/react-hooks';
import { LOGIN } from './login_query';
import { NavbarAuthProps } from '../navbar.model';
import { formatError } from '../../util/helpers/formatError';
import Dialog from '@material-ui/core/Dialog';
import DialogTitle from '@material-ui/core/DialogTitle';
import DialogContent from '@material-ui/core/DialogContent';

function LogIn({loadUser}: NavbarAuthProps) {
    const [open, setOpen] = React.useState(false);
    const [ login, { error } ] = useMutation(LOGIN, {
        onError: () => {},
        onCompleted: loadUser
    });

    return (
        <>
            <button onClick={() => setOpen(true)}>
            Log In
            </button>
            <Dialog open={open} onClose={() => setOpen(false)} aria-labelledby="form-dialog-title">
                <DialogTitle id="form-dialog-title">Log In</DialogTitle>
                <DialogContent>
                    <Formik
                        initialValues={{name: '', password: ''}}
                        onSubmit={(values) => login({variables: values})}
                    >
                        <Form>
                            <Field name="name"></Field>
                            <ErrorMessage name="name"></ErrorMessage>
                            <Field type="password" name="password"></Field>
                            <ErrorMessage name="password"></ErrorMessage>
                            <button>Login</button>
                            {error && <span>{formatError(error)}</span>}
                        </Form>
                    </Formik>
                </DialogContent>
            </Dialog>
        </>
    )
}

export default LogIn;
