import React from 'react';
import Slider from '@material-ui/core/Slider';
import { makeStyles, Theme } from '@material-ui/core';
import { createStyles } from '@material-ui/styles'
import { debounce } from 'lodash'
import { useMutation, useSubscription } from '@apollo/react-hooks';
import { UPDATE_INGREDIENTS, INGREDIENTS_SUBSCRIPTION } from './my_ingredients.query';

interface MyIngredientsProps {
    ingredients: any[];
    listId: string;
    userId: string;
}

const useStyles = makeStyles((theme?: Theme) =>
  createStyles({
    root: {
      width: 200,
    },
  }),
);

function processIngredient(ingredient: any) {
    const requiredFields = ['amount', 'original', 'id', 'amountFill', 'unit'];
    return Object.fromEntries(
        Object.entries(ingredient)
            .filter(([key]) => requiredFields.some(field => field === key))
    );
}

function getVariables(amountFill: number, ingredient: any, userId: string, listId: string) {
    return {
        variables: {
            userId,
            id: listId,
            ingredients: [{...processIngredient(ingredient), amountFill}],
        }
    }
}

function MyIngredients({ ingredients, listId, userId }: MyIngredientsProps) {
    const classes = useStyles();
    const [ updateIngredients ] = useMutation(UPDATE_INGREDIENTS);
    const { data } = useSubscription(INGREDIENTS_SUBSCRIPTION, {
        variables: { userId }
    });
    const update = debounce(updateIngredients, 300);

    // ingredients = data?.length ? ingredients.map(ing => data.find(({id}: any) => id === ing.id) ?? ing) : ingredients
    console.log('data: ', data, userId);
    console.log(ingredients);

    return (
        <ul>
            {ingredients.map(({ id, amount, amountFill, unit, original}: any, i: number) => (
                <li className="columns" key={i}>
                    <div className="column">{original}</div>
                    <div className="column">
                        <Slider
                            marks={[{value: 0, label: `0 ${unit}`}, {value: amount, label: `${amount} ${unit}`}]}
                            valueLabelDisplay="auto"
                            className={classes.root}
                            min={0}
                            max={amount}
                            defaultValue={amountFill}
                            onChangeCommitted={(e, v) => update(getVariables(v as number, ingredients[i], userId, listId))}
                        />
                    </div>
                    <div className="column">{amountFill} {unit}</div>
                </li>
            ))}
        </ul>
    );
}

export default MyIngredients;
