import React from 'react';
import { useQuery, useLazyQuery, useSubscription } from '@apollo/react-hooks';
import { GET_SHOPPING_LIST, INGREDIENTS_SUBSCRIPTION } from './view_shopping_list.query';
import { useParams } from 'react-router-dom';
import { GET_RECIPE } from '../../recipe/recipe_view/recipe_view.query';
import { flattenSteps } from '../../util/helpers/flattenSteps';
import { CircularProgress } from '@material-ui/core';
import MyIngredients from './my_ingredients/MyIngredients';

function mapWsUpdates(ingredients: any[], updates: any[]) {
    if (!updates?.length) {
       return  ingredients;
    }
    return ingredients.map(ingredient => {
        const update = updates.find(({id}) => id === ingredient.id)
        return { ...ingredient, ...update } ?? ingredient;
    })
}

function ViewShoppingList() {
    const { id } = useParams();
    const [ getRecipe, { data: recipeData, loading: loadingRecipe } ] = useLazyQuery(GET_RECIPE)
    const { data, loading } = useQuery(GET_SHOPPING_LIST, {
        variables: { id },
        onCompleted: ({getShoppingListById: { recipeId }}) => getRecipe({variables: { recipeId }})
    });
    const { data: socketData } = useSubscription(INGREDIENTS_SUBSCRIPTION, {
        variables: { userId: '5e03a6a91841d1fce86f9292' },
    });

    const shoppingList = data?.getShoppingListById;
    // const [ ingredients, setIngredients ] =  React.useState<any>(shoppingList?.ingredients ?? [])
    const recipe = recipeData?.recipeById;

    return (
        <section className="section">
            {loading && <div className="container has-text-centered"><CircularProgress /></div>}
            {
                shoppingList &&
                <>
                    <h1 className="title has-text-centered">{shoppingList.recipeTitle}</h1>
                    <div className="columns is-two-thirds is-centered">
                        <MyIngredients
                            userId={'5e03a6a91841d1fce86f9292'}
                            ingredients={mapWsUpdates(shoppingList.ingredients, socketData?.shoppingListIngredients)}
                            listId={shoppingList.id}
                        />
                    </div>
                    <div className="container">
                        {loadingRecipe && <div className="container has-text-centered"><CircularProgress /></div>}
                        {
                            recipe &&
                            <>
                                <div className="columns is-centered">
                                    <img src={recipe.image} alt={recipe.title}/>
                                </div>
                                <div className="container is-one-quarter">
                                    {
                                        recipe.analyzedInstructions
                                            .reduce(flattenSteps, [])
                                            .map(({step}: {step: string}, i: number) => (
                                            <div key={i} className="box has-text-centered">{step}</div>
                                        ))
                                    }
                                </div>
                            </>
                        }
                    </div>
                </>
            }
        </section>
    );
}

export default ViewShoppingList;
