import React from 'react';
import { useQuery } from '@apollo/react-hooks';
import { ALL_USERS } from './friends.query';
import { CURRENT_USER } from '../navbar/authModule/auth_module_query';

function FriendsList() {
    const { data: all } = useQuery(ALL_USERS)
    const { data } = useQuery(CURRENT_USER);

    return (
        <section className="section">
            <h1 className="title has-text-centered">Connections</h1>
            <div className="columns is-centered">
                <div className="column has-text-centered is-one-quarter">
                    <h1 className="has-text-weight-bold subtitle has-text-centered">Other users</h1>
                    {
                        all?.users?.map((user: any) => 
                            <div className="box" key={user.id}>
                                <article className="media">
                                <div className="media-content">
                                    <div className="content">
                                        {user.name}
                                        <button className="button is-success">
                                            <span className="icon is-small">
                                            <i className="fas fa-check"></i>
                                            </span>
                                            <span>Save</span>
                                        </button>
                                    </div>
                                </div>
                                </article>
                            </div>
                        )
                    }
                </div>
                <div className="column has-text-centered is-one-quarter">
                    <h1 className="has-text-weight-bold subtitle has-text-centered">Your connections</h1>
                    {
                        data?.currentUser?.friends.map((user: any) => 
                            <div className="box" key={user.id}>
                                {user.id}: {user.name}
                            </div>
                        )
                    }
                </div>
            </div>
        </section>
    );
}

export default FriendsList;
