import React from 'react';
import { debounce } from 'lodash'
import { useMutation, useSubscription } from '@apollo/react-hooks';
import { UPDATE_INGREDIENTS } from './my_ingredients.query';
import IngredientSlider from './ingredientSlider/IngredientSlider';
import { INGREDIENTS_SUBSCRIPTION } from '../view_shopping_list.query';

interface MyIngredientsProps {
    ingredients: any[];
    listId: string;
    userId: string;
}

function processIngredient(ingredient: any) {
    const requiredFields = ['amount', 'original', 'id', 'amountFill', 'unit'];
    return Object.fromEntries(
        Object.entries(ingredient)
            .filter(([key]) => requiredFields.some(field => field === key))
    );
}

function getVariables(amountFill: number, ingredient: any, userId: string, listId: string) {
    return {
        variables: {
            userId,
            id: listId,
            ingredients: [{...processIngredient(ingredient), amountFill}],
        }
    }
}

function mapWsUpdates(ingredients: any[], updates: any[]) {
    if (!updates?.length) {
       return  ingredients;
    }
    return ingredients.map(ingredient => {
        const update = updates.find(({id}) => id === ingredient.id)
        return { ...ingredient, ...update } ?? ingredient;
    })
}

function MyIngredients({ ingredients, listId, userId }: MyIngredientsProps) {
    const [ updateIngredients ] = useMutation(UPDATE_INGREDIENTS);
    const update = debounce(updateIngredients, 300);

    const { data: socketData } = useSubscription(INGREDIENTS_SUBSCRIPTION, {
        variables: { userId },
    });

    const [ cachedIngredients, setIngredients ] = React.useState(mapWsUpdates(ingredients, socketData))
    console.log(cachedIngredients[0].amountFill, ingredients[0].amountFill)

    return (
        <ul>
            {Array.from(cachedIngredients).map(({ id, amount, amountFill, unit, original}: any, i: number) => (
                <li className="columns" key={i}>
                    <div className="column">{original}</div>
                    <div className="column">
                        <IngredientSlider
                            amount={amount}
                            amountFill={amountFill}
                            unit={unit}
                            update={v => {
                                const newIngredients = cachedIngredients;
                                newIngredients[i] = { ...cachedIngredients[i], amountFill: v };
                                setIngredients(newIngredients);
                                update(getVariables(v as number, cachedIngredients[i], userId, listId))
                            }}
                        />
                    </div>
                    <div className="column">{amountFill} {unit}</div>
                </li>
            ))}
        </ul>
    );
}

export default MyIngredients;
