import React from 'react';
import { useQuery } from '@apollo/react-hooks';
import { NavLink } from 'react-router-dom';
import { GET_MY_SHOPPING_LISTS } from './my-shopping-list.queries'
import { CircularProgress } from '@material-ui/core';

function MyShoppingLists() {
    const { data, loading } = useQuery(GET_MY_SHOPPING_LISTS, { variables: { userId: '5e03a6a91841d1fce86f9292' } });

    return (
        <section className="section">
            <h1 className="title has-text-centered">My shopping lists</h1>
            <div className="container">
                { loading &&  <div className="is-centered"><CircularProgress /></div>}
                {
                    data && data.getUserShoppingLists &&
                    data.getUserShoppingLists.map((list: any) => (
                        <div key={list.id} className="columns is-two-thirds is-centered">
                            <NavLink className="container" to={`/shopping-list/${list.id}`}>
                                <div className="box colums">
                                    <div className="subtitle has-text-centered">{list.recipeTitle}</div>
                                    <div className="has-text-centered">
                                        <ul>
                                            {list.ingredients.map(({amount, unit, original}: any, i: number) => (
                                                <li key={i}>
                                                    <strong>{original}</strong> - <i>{amount} {unit}</i>
                                                </li>
                                            ))}
                                        </ul>
                                    </div>
                                </div>
                            </NavLink>
                        </div>
                    ))
                }
            </div>
        </section>
    )
}

export default MyShoppingLists;
