import { gql } from 'apollo-boost';

export const LOGIN = gql`
    mutation LogIn($name: String!, $password: String!) {
        logIn(password: $password, name: $name) {
            id
            name
        }
    }
`

export const USER_FRAGMENT = gql`
    fragment user on currentUser {
        id
        name
    }
`
