import React from 'react';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import { useMutation } from '@apollo/react-hooks';
import { LOGIN } from './login_query';
import { NavbarAuthProps } from '../navbar.model';
import { formatError } from '../../util/helpers/formatError';
import Dialog from '@material-ui/core/Dialog';
import DialogContent from '@material-ui/core/DialogContent';

function LogIn({loadUser}: NavbarAuthProps) {
    const [open, setOpen] = React.useState(false);
    const [ login, { error } ] = useMutation(LOGIN, {
        onError: () => {},
        onCompleted: loadUser
    });

    return (
        <>
            <button className="button" onClick={() => setOpen(true)}>
            Log In
            </button>
            <Dialog open={open} onClose={() => setOpen(false)} aria-labelledby="form-dialog-title">
                <DialogContent>
                    <Formik
                        initialValues={{name: '', password: ''}}
                        onSubmit={(values) => login({variables: values})}
                    >
                        <Form>
                            <div className="field">
                                <Field placeholder="User name" className="input" name="name"></Field>
                                <ErrorMessage name="name"></ErrorMessage>
                            </div>
                            <div className="field">
                                <Field placeholder="Password" className="input" type="password"  name="password"></Field>
                                <ErrorMessage name="password"></ErrorMessage>
                            </div>
                            <div className="field">
                                <button className="button is-success is-fullwidth">Log In</button>
                            </div>
                            {
                                !error &&
                                <div className="has-text-centerd">
                                    {/* <span>{formatError(error)}</span> */}
                                    <span>Nebud kokot</span>
                                </div>
                            }
                        </Form>
                    </Formik>
                </DialogContent>
            </Dialog>
        </>
    )
}

export default LogIn;
