import React from 'react';
import { NavLink } from 'react-router-dom';
import { useQuery, useMutation } from '@apollo/react-hooks';
import { CURRENT_USER } from './nav_bar_query'
import Login from './login/Login';
// import './NavBar.css'

function NavBar() {
    const { data, loading } = useQuery(CURRENT_USER);

    const user = data?.currentUser;

    return (
        <nav className="navbar has-background-light">
             <div className="navbar-menu">
                <div className="navbar-start">
                    <NavLink className="navbar-item" to="/">Home</NavLink>
                    <NavLink className="navbar-item" to="/search">Search</NavLink>
                    <NavLink className="navbar-item" to="/shopping-lists">Shopping Lists</NavLink>
                    <NavLink className="navbar-item" to="/random">Random Recipe</NavLink>
                </div>
                <div className="navbar-end">
                    <div className="navbar-item">{loading && '...loading'}</div>
                    <div className="navbar-item">{user && user.name}</div>
                    {JSON.stringify(user)}
                    {!loading && !user && <div className="navbar-item"><Login /></div>}
                </div>
             </div>
        </nav>
    );
}

export default NavBar;
