import { gql } from 'apollo-boost';

export const CURRENT_USER = gql`
    query CurrentUSer {
        currentUser {
            id
            name
            profilePicUrl
        }
    }
`;

export const LOGIN = gql`
    mutation LogIn($string: String!) {
        logIn(password: $string, name: $string) {
            id
            name
            profilePicUrl
        }
    }
`
