import { ApolloError } from 'apollo-boost';
import React, { ReactNode } from 'react';
import { NavLink } from 'react-router-dom';
import { CircularProgress } from '@material-ui/core';

type RecipesByIngredientProps = {
    loading: boolean,
    data: any,
    error: ApolloError | undefined,
    called: boolean
}


function renderIngredients(title: string, ingredientList: [{original: string}]): ReactNode {
    return ingredientList.length > 0 && (
        <div className="content has-text-centered">
        <strong>{title}</strong>
        <ul>{ingredientList.map(({original}, i) => <li key={i}><i>{original}</i></li>)}</ul>
        </div>
    )
}

export default function RecipesByIngredient({ called, loading, data, error }: RecipesByIngredientProps) {
    data = {
        recipesByIngredients: [
            {
                id: 1,
                title: 'Pasta with cheese',
                missedIngredients: [{original: 'cheese'}, {original: 'truffles'}],
                usedIngredients: [{original: 'pasta'}, {original: 'olive oil'}],
            }
        ]
    }

    return (
        <div className="container">
        { called && loading && <div className="is-centered"><CircularProgress /></div> }
        {   
            data && data.recipesByIngredients.length > 0 && !loading && !error &&
            <>{data.recipesByIngredients.map(({ id, title, missedIngredients, usedIngredients }: any, i: number) => {
                return (
                    <div key={i} className="card is-6 is-centered">
                        <header className="card-title has-text-centered"><p className="subtitle">{title}</p></header>
                        <div className="card-content"> 
                            {renderIngredients('Used Ingredients', usedIngredients)}
                            {renderIngredients('Missed Ingredients', missedIngredients)}
                        </div>
                        <div className="card-footer"><NavLink className="button is-link card-footer-item" to={`/recipe/${id}`}>View Recipe</NavLink></div>
                    </div>
                )
            })}</>
        }
        </div>
    );
}
