import { gql } from 'apollo-boost';

export const UPDATE_INGREDIENTS = gql`
    mutation UpdateIngredients(
        $userId: String!
        $ingredients: [IngredientInput!]!
        $id: String!
    ) {
        updateShoppingList(
            userId: $userId
            ingredients: $ingredients
            id: $id
        ) {
            ingredients {
                id
                original
                amount
                amountFill
                unit
            }
        }
    }
`

